BEGIN TRANSACTION;
CREATE TABLE IF NOT EXISTS "clientes" (
	"id"	INTEGER,
	"nombre"	TEXT,
	"empresa"	TEXT,
	"telefono"	TEXT,
	"email"	TEXT UNIQUE,
	"creacion"	TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
	"modificacion"	TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
	"provincia"	TEXT,
	"localidad"	TEXT,
	"direccion"	TEXT,
	PRIMARY KEY("id" AUTOINCREMENT)
);
CREATE TABLE IF NOT EXISTS "usuarios" (
	"id"	INTEGER,
	"nombre"	TEXT,
	"apellido"	TEXT,
	"contrasena"	TEXT,
	"rol"	TEXT NOT NULL CHECK("rol" IN ('admin', 'tecnico')),
	"activo"	INTEGER DEFAULT 1,
	"creacion"	TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
	"modificacion"	TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
	PRIMARY KEY("id" AUTOINCREMENT)
);
CREATE TABLE IF NOT EXISTS "orden_trabajo" (
	"id"	INTEGER,
	"id_cliente"	INTEGER NOT NULL,
	"id_usuario"	INTEGER NOT NULL,
	"importancia"	TEXT NOT NULL CHECK("importancia" IN ('alta', 'media', 'baja')),
	"descripcion"	TEXT,
	"estado"	TEXT NOT NULL DEFAULT 'pendiente' CHECK("estado" IN ('pendiente', 'en proceso', 'finalizado')),
	"creacion"	TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
	"modificacion"	TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
	PRIMARY KEY("id" AUTOINCREMENT),
	FOREIGN KEY("id_usuario") REFERENCES "usuarios"("id") ON DELETE CASCADE,
	FOREIGN KEY("id_cliente") REFERENCES "clientes"("id") ON DELETE CASCADE
);
CREATE TABLE IF NOT EXISTS "reclamos" (
	"id"	INTEGER,
	"usuario_id"	INTEGER,
	"ordenTrabajo_id"	INTEGER,
	"titulo"	TEXT,
	"descripcion"	TEXT,
	"importancia"	TEXT CHECK("importancia" IN ('alta', 'media', 'baja')),
	"estado"	TEXT CHECK("estado" IN ('abierto', 'en proceso', 'cerrado')),
	"fecha_resolucion"	TIMESTAMP,
	"created_at"	TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
	"updated_at"	TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
	PRIMARY KEY("id" AUTOINCREMENT),
	FOREIGN KEY("usuario_id") REFERENCES "usuarios"("id"),
	FOREIGN KEY("ordenTrabajo_id") REFERENCES "orden_trabajo"("id")
);
COMMIT;
